 public void placeRandomObstacles(){
  //Initial obstacle position
  circleDrawRad.clear();
  circlePos.clear();
  circleColor.clear();
  circleDrawRad.add(30.0f); //Make the first obstacle big
  circleColRad.add(30.0f+agentColRad);
  circlePos.add(new Vec3(
      random(-sceneX*0.5f+obstacleWallPadding,sceneX*0.5f-obstacleWallPadding),
      -circleDrawRad.get(0),
      random(-sceneZ*0.5f,sceneZ*0.5f)
  ));
  circleColor.add(new Vec3(
      random(255),
      random(255),
      random(255)
  ));

  for (int i = 1; i < numObstacles; i++){
    float rad = (10+40*pow(random(1),3));
    circleDrawRad.add(rad);
    circleColRad.add(rad+agentColRad);
    circlePos.add(new Vec3(
        random(-sceneX*0.5f+obstacleWallPadding,sceneX*0.5f-obstacleWallPadding),
        -circleDrawRad.get(i),
        random(-sceneZ*0.5f+obstacleWallPadding,sceneZ*0.5f-obstacleWallPadding)
    ));
    circleColor.add(new Vec3(
        random(255),
        random(255),
        random(255)
    ));
  }
}

 public void reset() {
    placeRandomObstacles();
    kiwiTime = 0;
    kiwiSwitchFrame = 0;
    currFrame = 0;
    initiatePathfinding();
}

 public void initiatePathfinding() {
    for (int i = 0; i < circlePos.size(); i++) {
        Vec3 pos = circlePos.get(i);
        float rad = circleColRad.get(i);
        if (pos.y > -agentColRad*2-circleDrawRad.get(i)) {
            validCircles[i] = true;
        } 
        circlePosArr[i] = new Vec2(pos.x, pos.z);
        circleRadArr[i] = rad;
    }
    testPRM();
    println(curPath);
    indexCounter = 1;
    startNode = curPath.get(0);
    currNode = startNode; 
    nextNode = curPath.get(1);
    goalNode = curPath.get(curPath.size()-1);
    currPos = nodePos[currNode];
    nextPos = nodePos[nextNode];
    agentVel = nextPos.minus(currPos).normalized().times(goalSpeed);
}
 public void update(float dt) {
    println(agentPos.distanceTo(nextPos));
    if (agentPos.distanceTo(nextPos) < 50) {
        if (nextNode == goalNode) {
            agentPos = new Vec3(goalPos.x, kiwiYOffset, goalPos.y);
            agentVel = new Vec2(0,0);
        } else {
            indexCounter++;
            currNode = nextNode;
            currPos = nextPos;
            nextNode = curPath.get(indexCounter);
            nextPos = nodePos[nextNode];
            agentVel = nextPos.minus(currPos);
            agentVel = agentVel.normalized().times(goalSpeed);
        }
    }
    agentPos.add(agentVel.times(dt));
}

 public void updateKiwiFrame() {
    kiwiTime += agentVel.length()*0.005f;
    if (kiwiTime > 1/kiwi_framerate) {
        kiwiTime = 0;
        kiwiSwitchFrame++;
        if (kiwiSwitchFrame == times[currFrame]) {
            kiwiSwitchFrame = 0;
            currFrame = (currFrame+1)%kiwiFrames;
        }
    }
}
//Vector Library
//CSCI 5611 Vector 2 Library [Example]
// Stephen J. Guy <sjguy@umn.edu>

public class Vec2 {
  public float x, y;
  
  public Vec2(float x, float y){
    this.x = x;
    this.y = y;
  }
  
  public String toString(){
    return "(" + x+ "," + y +")";
  }
  
  public float length(){
    return sqrt(x*x+y*y);
  }
  
  public float lengthSqr(){
    return x*x+y*y;
  }
  
  public Vec2 plus(Vec2 rhs){
    return new Vec2(x+rhs.x, y+rhs.y);
  }
  
  public void add(Vec2 rhs){
    x += rhs.x;
    y += rhs.y;
  }
  
  public Vec2 minus(Vec2 rhs){
    return new Vec2(x-rhs.x, y-rhs.y);
  }
  
  public void subtract(Vec2 rhs){
    x -= rhs.x;
    y -= rhs.y;
  }
  
  public Vec2 times(float rhs){
    return new Vec2(x*rhs, y*rhs);
  }
  
  public void mul(float rhs){
    x *= rhs;
    y *= rhs;
  }
  
  public void clampToLength(float maxL){
    float magnitude = sqrt(x*x + y*y);
    if (magnitude > maxL){
      x *= maxL/magnitude;
      y *= maxL/magnitude;
    }
  }
  
  public void setToLength(float newL){
    float magnitude = sqrt(x*x + y*y);
    x *= newL/magnitude;
    y *= newL/magnitude;
  }
  
  public void normalize(){
    float magnitude = sqrt(x*x + y*y);
    x /= magnitude;
    y /= magnitude;
  }
  
  public Vec2 normalized(){
    float magnitude = sqrt(x*x + y*y);
    return new Vec2(x/magnitude, y/magnitude);
  }
  
  public float distanceTo(Vec2 rhs){
    float dx = rhs.x - x;
    float dy = rhs.y - y;
    return sqrt(dx*dx + dy*dy);
  }
}

 public Vec2 interpolate(Vec2 a, Vec2 b, float t){
  return a.plus((b.minus(a)).times(t));
}

 public float dot(Vec2 a, Vec2 b){
  return a.x*b.x + a.y*b.y;
}

 public Vec2 projAB(Vec2 a, Vec2 b){
  return b.times(a.x*b.x + a.y*b.y);
}
